import React from "react";
import {createUseStyles} from "react-jss";

const useStyles = createUseStyles({
  card: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-around",
    borderRadius: "5px",
    border: "2px solid rgba(53, 59, 64, 0.66)",
    boxShadow: "5px 5px 10px gray",
    width: "max-content",
    maxWidth: "350px",
    height: "min-content",
    margin: "10px"
  },
  cardText: {
    flexGrow: "1",
    wordWrap: "break-word",
    textAlign: "center"
  },
  postHeader: {
    display: "flex",
    flexDirecton: "row",
    justifyContent: "flex-end"
  },
  "@keyframes disappear": {
    from: {transform: "none"},
    to: {transform: "scale(0.5)", opacity: "0"}
  },
  dissapearing: {
    animation: "$disappear 0.6s ease-in-out 1"
  },
  "@keyframes appear": {
    from: {transform: "scale(0.1)", opacity: "100"},
    to: {transform: "none", opacity: "0"}
  },
  appearing: {
    animation: "$appear 0.5s ease-in-out 1"
  }
});

function ReqCard(props) {
  const classes = useStyles();
  return (
    <React.Fragment>
      <div className={classes.card}>
        <div className={classes.cardText}>requested num - {props.num} </div>
        <div className={classes.cardText}>result - {props.fibNum} </div>
        <div className={classes.cardText}>
          {" "}
          date -{" "}
          {() => {
            let date = new Date(Date.parse(props.date));
            return date.toDateString();
          }}
        </div>
      </div>
    </React.Fragment>
  );
}

export default ReqCard;
